# add an address you want to filter pending transactions for
# make sure the address is in the correct format
router = web3.toChecksumAddress('0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D')
Contract = web3.eth.contract(address=router, abi=abi)


def handle_event(event):
    try:
        transaction_json = Web3.toJSON(event).strip('"')
        transaction = json.loads(transaction_json)
        input_data = transaction['input']
        if transaction['to'] == router:
            decode = Contract.decode_function_input(input_data)
            path_addresses = decode[1]['path']
            token_address_buy = path_addresses[1]
            token = Token.query.filter_by(contact_address=token_address_buy).first()

            if token:
                token.rank += 1
                db.session.commit()
                print(token)
            else:
                print('Not what we are looking for')

    except Exception as err:
        # Print transactions with errors. Expect to see transactions submitted with errors
        print(f'Error: {err}')

def log_loop(poll_interval):
    latest_block = web3.eth.block_number

    while True:
        try:
            # Get the latest block number
            current_block = web3.eth.block_number

            if current_block > latest_block:
                # Iterate over the transactions in the latest block
                block = web3.eth.get_block(current_block)
                for tx in block.transactions:
                    # Get the transaction details
                    transaction = web3.eth.get_transaction(tx.hex())

                    handle_event(transaction)

                # Update the latest block number
                latest_block = current_block

            time.sleep(poll_interval)
        except Exception as err:
            # Handle exceptions gracefully
            print(f'Error: {err}')
            time.sleep(poll_interval)


def main():
    with APP.app_context():
        poll_interval = 1  # Adjust the poll interval as needed
        log_loop(poll_interval)

if __name__ == '__main__':
    with APP.app_context():
        if not os.path.exists(db_path):
            db.create_all()  # Create the database tables

        monitor_thread = Thread(target=main)
        monitor_thread.start()

        # Run the Flask application
        APP.run(debug=True, threaded=True)